const name = $('Set API Sheet Variables').first().json.name;

const data = $input.all()[0]?.json.data ?? []

const filteredData = data?.filter((d)=> d?.name === name)

return filteredData

const data = $input.all()[0]?.json.data ?? []

const symbols = data.reduce((acc, cur, index) => {
  if(index + 1 === data?.length) {
     return acc + cur.symbol
  }

  return acc + cur.symbol +','
}, '')

return [{json: {symbols}}];
