// 1. $now를 JavaScript Date 객체로 변환
const now = new Date($now);

console.log(now)

// 2. 년, 월, 일 각각을 변수로 추출
const year = now.getFullYear() + '';            // ex) 2025
const month = now.getMonth() + 1;          // ex) 4 (주의: getMonth()는 0부터 시작함)
const day = now.getDate();                 // ex) 5

// 3. 월과 일이 한 자리 수일 경우 앞에 0 붙이기 (예: 4 → '04')
const formattedMonth = String(month).padStart(2, '0'); // padStart(2, '0') 사용
const formattedDay = String(day).padStart(2, '0');

// 4. YYYY-MM-DD 형식의 문자열 만들기
const todayFormatted = `${year}-${formattedMonth}-${formattedDay}`;

// 5. n8n에 반환할 결과
return [{
  json: {
    year: year,
    month: formattedMonth,
    day: formattedDay,
    today: todayFormatted
  }
}];
