// 워크플로우 데이터 유효성 검증 및 처리
const items = $input.all();
const validItems = [];

for (const item of items) {
  if (!item.json || !item.json.name) {
    // 이름이 없는 워크플로우는 건너뛰기
    continue;
  }

  // 워크플로우 이름에서 파일명으로 사용할 수 없는 문자 제거
  const safeName = item.json.name.replace(/[\/?*:;{}\\\[\]]/g, '_');

  // 안전한 파일명 추가
  item.json.safeName = safeName;

  validItems.push(item);
}

// 전체 워크플로우 수 업데이트
const stats = $node["Globals"].json.backup_stats;
stats.total = validItems.length;

return [
  {
    json: {
      repo: $node["Globals"].json.repo,
      timestamp: $node["Globals"].json.timestamp,
      backup_stats: stats,
      workflows: validItems.length
    }
  },
  ...validItems
];

let items = $input.all();

for (const item of items) {
    try {
        // Base64 디코딩
        if (item.json.content) {
            item.json.content = Buffer.from(item.json.content, 'base64').toString('utf8');
        }

        // 오류 표시 제거
        if (item.json.error) {
            delete item.json.error;
        }
    } catch (error) {
        // 디코딩 오류 처리
        item.json.error = `Decoding error: ${error.message}`;
    }
}

return items;

// 백업 결과 통계 업데이트
const stats = $node["Globals"].json.backup_stats;
const item = $input.first();

// 파일 생성 결과 처리
if (item.json.error) {
  stats.failed++;
} else {
  stats.created++;
}

return [
  {
    json: {
      workflow: $('Loop Over Items').item.json.name,
      operation: "create",
      status: item.json.error ? "failed" : "success",
      error: item.json.error,
      stats: stats
    }
  }
];

// 백업 결과 통계 업데이트
const stats = $node["Globals"].json.backup_stats;
const item = $input.first();

// 파일 업데이트 결과 처리
if (item.json.error) {
  stats.failed++;
} else {
  stats.updated++;
}

return [
  {
    json: {
      workflow: $('Loop Over Items').item.json.name,
      operation: "update",
      status: item.json.error ? "failed" : "success",
      error: item.json.error,
      stats: stats
    }
  }
];

// 변경 없음 상태 처리
const stats = $node["Globals"].json.backup_stats;
stats.unchanged++;

return [
  {
    json: {
      workflow: $('Loop Over Items').item.json.name,
      operation: "check",
      status: "unchanged",
      stats: stats
    }
  }
];

// 파일 존재하지 않음 처리
const stats = $node["Globals"].json.backup_stats;
stats.failed++;

return [
  {
    json: {
      workflow: $('Loop Over Items').item.json.name,
      operation: "check",
      status: "error",
      error: $input.first().json.error,
      stats: stats
    }
  }
];

// 최종 백업 결과 요약
const items = $input.all();
const firstItem = items[0];

// 백업 통계 수집
const stats = firstItem.json.stats;

// 이메일로 보낼 내용 생성
const timestamp = new Date().toISOString();
const summary = {
  json: {
    timestamp: timestamp,
    backup_results: {
      total_workflows: stats.total,
      created: stats.created,
      updated: stats.updated,
      unchanged: stats.unchanged,
      failed: stats.failed,
      success_rate: `${Math.round(((stats.created + stats.updated + stats.unchanged) / stats.total) * 100)}%`
    },
    failed_workflows: items
      .filter(item => item.json.status === "failed" || item.json.status === "error")
      .map(item => ({
        name: item.json.workflow,
        operation: item.json.operation,
        error: item.json.error
      }))
  }
};

return [summary];

